// src/components/Footer.tsx
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { staggerItem, fadeInUp } from '../hooks/useAnimations';
import '../styles/globals.css';

const Footer: React.FC = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <footer className="footer">
      {/* Upper part of the two-faced footer */}
      <div className="footer-upper glass">
        <div className="container">
          <div className="footer-grid">
            {/* Left Column - About */}
            <motion.div 
              className="footer-section"
              variants={staggerItem}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <motion.h3 
                className="footer-title red-accent"
                variants={fadeInUp}
              >
                About QuranHub
              </motion.h3>
              <motion.p 
                className="footer-text"
                variants={fadeInUp}
                transition={{ delay: 0.1 }}
              >
                Providing access to the Holy Quran with beautiful interface and smooth animations. 
                Explore verses, Surahs, and Islamic resources with our aesthetic platform.
              </motion.p>
              <motion.div 
                className="social-links"
                variants={fadeInUp}
                transition={{ delay: 0.2 }}
              >
                <a href="#" className="social-link">Facebook</a>
                <a href="#" className="social-link">Twitter</a>
                <a href="#" className="social-link">Instagram</a>
              </motion.div>
            </motion.div>

            {/* Right Column - Links */}
            <motion.div 
              className="footer-section"
              variants={staggerItem}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              <motion.h3 
                className="footer-title red-accent"
                variants={fadeInUp}
              >
                Quick Links
              </motion.h3>
              <motion.ul 
                className="footer-links"
                variants={staggerItem}
                initial="hidden"
                whileInView="visible"
                viewport={{ once: true }}
              >
                {[
                  { name: 'Home', url: '#' },
                  { name: 'Surahs', url: '#' },
                  { name: 'Juz', url: '#' },
                  { name: 'About', url: '#' },
                  { name: 'Resources', url: '#' },
                  { name: 'Contact', url: '#' }
                ].map((link, index) => (
                  <motion.li 
                    key={link.name}
                    variants={fadeInUp}
                    transition={{ delay: 0.1 * index }}
                  >
                    <a href={link.url} className="footer-link">
                      {link.name}
                    </a>
                  </motion.li>
                ))}
              </motion.ul>
            </motion.div>
          </div>
        </div>
      </div>

      {/* Lower part of the two-faced footer - appears on scroll */}
      <motion.div 
        className={`footer-lower glass ${scrolled ? 'visible' : ''}`}
        initial={false}
        animate={{ y: scrolled ? 0 : 50, opacity: scrolled ? 1 : 0 }}
        transition={{ duration: 0.5, ease: "easeOut" }}
      >
        <div className="container">
          <div className="footer-quran-section">
            <motion.h2 
              className="footer-quran-title"
              initial={{ scale: 0.8 }}
              animate={{ scale: scrolled ? 1 : 0.8 }}
              transition={{ duration: 0.5 }}
            >
              Quran
            </motion.h2>
            <motion.p 
              className="footer-quran-subtitle"
              animate={{ opacity: scrolled ? 1 : 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              Guidance and wisdom for all humanity
            </motion.p>
          </div>
        </div>
      </motion.div>

      {/* Footer bottom */}
      <div className="footer-bottom">
        <div className="container">
          <motion.p 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            Â© {new Date().getFullYear()} QuranHub. All rights reserved.
          </motion.p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
