// src/pages/HomePage.tsx
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { staggerContainer, staggerItem, fadeInUp, smoothFloat } from '../hooks/useAnimations';
import './HomePage.css';

interface Chapter {
  id: number;
  name_simple: string;
  translated_name: {
    language_name: string;
    name: string;
  };
  verses_count: number;
  revelation_type: string;
}

const HomePage: React.FC = () => {
  // Sample Quran verses (Ayat)
  const quranVerses = [
    {
      id: 1,
      arabic: "إِنَّمَا ٱلْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَوَيْكُمْ ۚ وَٱتَّقُوا ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ",
      translation: "The believers are but brothers, so make settlement between your brothers. And fear Allah that you may receive mercy.",
      surah: "Al-Hujurat 49:10"
    },
    {
      id: 2,
      arabic: "وَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيزَانَ إِنْ نَقَصْنَاكُمْ مِنْ شَيْءٍ فَإِنَّمَا نَخْسِرُ فِي الْأَمَانَةِ",
      translation: "And do not diminish the measure and the scale. If we gave you little, indeed, we are cheaters in trust.",
      surah: "Al-Hijr 15:18"
    },
    {
      id: 3,
      arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَجَاهِدُوا فِي سَبِيلِهِ",
      translation: "O you who have believed, fear Allah and seek from Him the means of approach, and strive in His cause.",
      surah: "Al-Ma'idah 5:35"
    }
  ];

  const rotatingWords = ['Guidance', 'Wisdom', 'Peace', 'Understanding', 'Comfort'];
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [chapters, setChapters] = useState<Chapter[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentWordIndex((prevIndex) => (prevIndex + 1) % rotatingWords.length);
    }, 2000); // Change word every 2 seconds

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Fetch chapters from Quran API
    const fetchChapters = async () => {
      try {
        const response = await fetch('https://api.quran.com/api/v4/chapters');
        const data = await response.json();
        setChapters(data.chapters);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching chapters:', error);
        setLoading(false);

        // Fallback data in case API fails
        const fallbackChapters: Chapter[] = Array.from({ length: 114 }, (_, i) => ({
          id: i + 1,
          name_simple: `Surah ${i + 1}`,
          translated_name: {
            language_name: 'English',
            name: `Chapter ${i + 1}`
          },
          verses_count: Math.floor(Math.random() * 286) + 1, // Random between 1-286
          revelation_type: i < 86 ? 'Meccan' : 'Medinan'
        }));
        setChapters(fallbackChapters);
      }
    };

    fetchChapters();
  }, []);

  return (
    <div className="home-page">
      {/* Enhanced decorative background elements */}
      <div className="hero-background-elements">
        <motion.div 
          className="hero-shape hero-shape-1"
          variants={smoothFloat}
          initial="hidden"
          animate="visible"
          transition={{ duration: 8, repeat: Infinity, repeatType: "reverse" }}
        ></motion.div>
        <motion.div 
          className="hero-shape hero-shape-2"
          variants={smoothFloat}
          initial="hidden"
          animate="visible"
          transition={{ duration: 10, repeat: Infinity, repeatType: "reverse", delay: 1 }}
        ></motion.div>
        <motion.div 
          className="hero-shape hero-shape-3"
          variants={smoothFloat}
          initial="hidden"
          animate="visible"
          transition={{ duration: 12, repeat: Infinity, repeatType: "reverse", delay: 2 }}
        ></motion.div>
      </div>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <motion.div 
            className="hero-content"
            variants={staggerContainer}
            initial="hidden"
            animate="visible"
          >
            <motion.h1 
              className="hero-title"
              variants={fadeInUp}
            >
              Not sure what to read in Quran?
            </motion.h1>

            <motion.p 
              className="hero-subtitle"
              variants={fadeInUp}
              transition={{ delay: 0.2 }}
            >
              Discover meaningful <span className="rotating-word red-accent">{rotatingWords[currentWordIndex]}</span>
            </motion.p>

            <motion.div
              className="hero-description"
              variants={fadeInUp}
              transition={{ delay: 0.4 }}
            >
              <p>
                Our platform helps those who are new to the Quran or unsure where to start. 
                Explore carefully selected verses, thematic collections, and guided readings.
              </p>
            </motion.div>

            <motion.div
              className="hero-buttons"
              variants={fadeInUp}
              transition={{ delay: 0.6 }}
            >
              <button className="btn btn-primary">Discover Verses</button>
              <button className="btn btn-outline">Learn More</button>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Search Section */}
      <section className="search-section">
        <div className="container">
          <motion.div
            className="search-content"
            variants={fadeInUp}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
          >
            <h2 className="search-title red-accent">Find Your Guidance</h2>
            <div className="search-box-container">
              <input 
                type="text" 
                placeholder="Search for verses, topics, or themes..." 
                className="search-input"
              />
              <motion.button
                className="search-button"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Send
              </motion.button>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Chapters Section */}
      <section className="chapters-section">
        <div className="container">
          <motion.h2 
            className="section-title red-accent"
            variants={fadeInUp}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
          >
            All Quran Chapters (Surahs)
          </motion.h2>

          {loading ? (
            <div className="loading">Loading chapters...</div>
          ) : (
            <motion.div 
              className="chapters-grid"
              variants={staggerContainer}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true, amount: 0.2 }}
            >
              {chapters.map((chapter, index) => (
                <motion.div 
                  key={chapter.id}
                  className="chapter-card glass-animated"
                  variants={staggerItem}
                  whileHover={{ 
                    y: -5,
                    transition: { duration: 0.3 }
                  }}
                >
                  <div className="chapter-header">
                    <span className="chapter-number">{chapter.id}</span>
                    <span className="chapter-revelation-type">{chapter.revelation_type}</span>
                  </div>
                  <div className="chapter-content">
                    <h3 className="chapter-name">{chapter.name_simple}</h3>
                    <p className="chapter-translation">"{chapter.translated_name.name}"</p>
                    <p className="chapter-verses">{chapter.verses_count} verses</p>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}
        </div>
      </section>

      {/* About Section */}
      <section className="about-section">
        <div className="container">
          <motion.div 
            className="about-content"
            variants={staggerContainer}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          >
            <motion.div 
              className="about-text"
              variants={fadeInUp}
            >
              <motion.h2 
                className="red-accent" /* Keep this as red accent */
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.1 }}
              >
                Guidance for Every Journey
              </motion.h2>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.2 }}
              >
                Whether you're new to the Quran or looking to deepen your understanding, 
                our platform provides accessible resources to guide your spiritual journey.
              </motion.p>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.3 }}
              >
                Explore verses by theme, difficulty level, or spiritual need. Our curated 
                collections help you find meaningful passages even if you're not sure where to start.
              </motion.p>
            </motion.div>
            <motion.div 
              className="about-visual"
              variants={fadeInUp}
              transition={{ delay: 0.2 }}
            >
              <motion.div 
                className="glass-animated about-card"
                whileHover={{ 
                  scale: 1.03,
                  transition: { duration: 0.3 }
                }}
              >
                <motion.h3
                  className="black-title"
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: 0.4 }}
                >
                  How It Works
                </motion.h3>
                <motion.ul
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: 0.5 }}
                >
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Select your interest or need
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Receive personalized verse recommendations
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Explore with helpful translations
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Deepen your understanding gradually
                  </motion.li>
                </motion.ul>
              </motion.div>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Chapters Section */}
      <section className="chapters-section">
        <div className="container">
          <motion.h2 
            className="section-title red-accent"
            variants={fadeInUp}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
          >
            All Quran Chapters (Surahs)
          </motion.h2>

          <motion.div 
            className="chapters-grid"
            variants={staggerContainer}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          >
            {chapters.map((chapter, index) => (
              <motion.div 
                key={chapter.id}
                className="chapter-card glass-animated"
                variants={staggerItem}
                whileHover={{ 
                  y: -5,
                  transition: { duration: 0.3 }
                }}
              >
                <div className="chapter-header">
                  <span className="chapter-number">{chapter.id}</span>
                  <span className="chapter-revelation-type">{chapter.revelation_type}</span>
                </div>
                <div className="chapter-content">
                  <h3 className="chapter-name">{chapter.name_simple}</h3>
                  <p className="chapter-translation">"{chapter.translated_name.name}"</p>
                  <p className="chapter-verses">{chapter.verses_count} verses</p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
