// src/pages/HomePage.tsx
import React from 'react';
import { motion } from 'framer-motion';
import { staggerContainer, staggerItem, fadeInUp, smoothFloat } from '../hooks/useAnimations';
import './HomePage.css';

const HomePage: React.FC = () => {
  // Sample Quran verses (Ayat)
  const quranVerses = [
    {
      id: 1,
      arabic: "إِنَّمَا ٱلْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَوَيْكُمْ ۚ وَٱتَّقُوا ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ",
      translation: "The believers are but brothers, so make settlement between your brothers. And fear Allah that you may receive mercy.",
      surah: "Al-Hujurat 49:10"
    },
    {
      id: 2,
      arabic: "وَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيزَانَ إِنْ نَقَصْنَاكُمْ مِنْ شَيْءٍ فَإِنَّمَا نَخْسِرُ فِي الْأَمَانَةِ",
      translation: "And do not diminish the measure and the scale. If we gave you little, indeed, we are cheaters in trust.",
      surah: "Al-Hijr 15:18"
    },
    {
      id: 3,
      arabic: "يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَجَاهِدُوا فِي سَبِيلِهِ",
      translation: "O you who have believed, fear Allah and seek from Him the means of approach, and strive in His cause.",
      surah: "Al-Ma'idah 5:35"
    }
  ];

  return (
    <div className="home-page">
      {/* Background decorative elements */}
      <motion.div 
        className="fluid-shape shape-1" 
        variants={smoothFloat}
        initial="hidden"
        animate="visible"
      ></motion.div>
      <motion.div 
        className="fluid-shape shape-2" 
        variants={smoothFloat}
        initial="hidden"
        animate="visible"
        transition={{ delay: 0.5 }}
      ></motion.div>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <motion.div 
            className="hero-content"
            variants={staggerContainer}
            initial="hidden"
            animate="visible"
          >
            <motion.h1 
              className="hero-title red-accent"
              variants={fadeInUp}
            >
              Not sure what to read in Quran?
            </motion.h1>

            <motion.p 
              className="hero-subtitle"
              variants={fadeInUp}
              transition={{ delay: 0.2 }}
            >
              Discover meaningful verses tailored for you
            </motion.p>

            <motion.div
              className="hero-description"
              variants={fadeInUp}
              transition={{ delay: 0.4 }}
            >
              <p>
                Our platform helps those who are new to the Quran or unsure where to start. 
                Explore carefully selected verses, thematic collections, and guided readings.
              </p>
            </motion.div>

            <motion.div
              className="hero-buttons"
              variants={fadeInUp}
              transition={{ delay: 0.6 }}
            >
              <button className="btn btn-primary">Discover Verses</button>
              <button className="btn btn-outline">Learn More</button>
            </motion.div>
          </motion.div>
        </div>
      </section>

      {/* Featured Verses Section */}
      <section className="featured-verses">
        <div className="container">
          <motion.h2 
            className="section-title red-accent"
            variants={fadeInUp}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.3 }}
          >
            Featured Verses
          </motion.h2>

          <motion.div 
            className="verses-grid"
            variants={staggerContainer}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          >
            {quranVerses.map((verse, index) => (
              <motion.div 
                key={verse.id}
                className="verse-card glass-animated"
                variants={staggerItem}
                whileHover={{ 
                  y: -10,
                  transition: { duration: 0.3 }
                }}
              >
                <div className="verse-header">
                  <span className="verse-surah">{verse.surah}</span>
                </div>
                <div className="verse-content">
                  <p className="quran-text">{verse.arabic}</p>
                  <p className="verse-translation">{verse.translation}</p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </section>

      {/* About Section */}
      <section className="about-section">
        <div className="container">
          <motion.div 
            className="about-content"
            variants={staggerContainer}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          >
            <motion.div 
              className="about-text"
              variants={fadeInUp}
            >
              <motion.h2 
                className="red-accent"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.1 }}
              >
                Guidance for Every Journey
              </motion.h2>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.2 }}
              >
                Whether you're new to the Quran or looking to deepen your understanding, 
                our platform provides accessible resources to guide your spiritual journey.
              </motion.p>
              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: 0.3 }}
              >
                Explore verses by theme, difficulty level, or spiritual need. Our curated 
                collections help you find meaningful passages even if you're not sure where to start.
              </motion.p>
            </motion.div>
            <motion.div 
              className="about-visual"
              variants={fadeInUp}
              transition={{ delay: 0.2 }}
            >
              <motion.div 
                className="glass-animated about-card"
                whileHover={{ 
                  scale: 1.03,
                  transition: { duration: 0.3 }
                }}
              >
                <motion.h3
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: 0.4 }}
                >
                  How It Works
                </motion.h3>
                <motion.ul
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  viewport={{ once: true }}
                  transition={{ delay: 0.5 }}
                >
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Select your interest or need
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Receive personalized verse recommendations
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Explore with helpful translations
                  </motion.li>
                  <motion.li
                    whileHover={{ x: 5 }}
                    transition={{ duration: 0.2 }}
                  >
                    Deepen your understanding gradually
                  </motion.li>
                </motion.ul>
              </motion.div>
            </motion.div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default HomePage;
