// src/components/sections/ChaptersSection.tsx
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { staggerContainer, staggerItem, fadeInUp } from '../../hooks/useAnimations';
import './ChaptersSection.css';

interface Chapter {
  id: number;
  name_simple: string;
  translated_name: {
    language_name: string;
    name: string;
  };
  verses_count: number;
  revelation_type: string;
}

interface ChaptersSectionProps {
  children?: React.ReactNode;
}

const ChaptersSection: React.FC<ChaptersSectionProps> = () => {
  const [chapters, setChapters] = useState<Chapter[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch chapters from Quran API
    const fetchChapters = async () => {
      try {
        // Using the public API endpoint for chapters
        const response = await fetch('https://api.quran.com/api/v4/chapters');
        const data = await response.json();

        // Check if the response has the expected structure
        if (data && data.chapters) {
          setChapters(data.chapters);
        } else {
          console.error('Unexpected API response structure:', data);
          // Fallback data
          const fallbackChapters: Chapter[] = Array.from({ length: 114 }, (_, i) => ({
            id: i + 1,
            name_simple: `Surah ${i + 1}`,
            translated_name: {
              language_name: 'English',
              name: `Chapter ${i + 1}`
            },
            verses_count: Math.floor(Math.random() * 286) + 1, // Random between 1-286
            revelation_type: i < 86 ? 'Meccan' : 'Medinan'
          }));
          setChapters(fallbackChapters);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching chapters:', error);
        setLoading(false);

        // Fallback data in case API fails
        const fallbackChapters: Chapter[] = Array.from({ length: 114 }, (_, i) => ({
          id: i + 1,
          name_simple: `Surah ${i + 1}`,
          translated_name: {
            language_name: 'English',
            name: `Chapter ${i + 1}`
          },
          verses_count: Math.floor(Math.random() * 286) + 1, // Random between 1-286
          revelation_type: i < 86 ? 'Meccan' : 'Medinan'
        }));
        setChapters(fallbackChapters);
      }
    };

    fetchChapters();
  }, []);

  return (
    <section className="chapters-section">
      <div className="container">
        <motion.h2 
          className="section-title red-accent"
          variants={fadeInUp}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.3 }}
        >
          All Quran Chapters (Surahs)
        </motion.h2>

        {loading ? (
          <div className="loading">Loading chapters...</div>
        ) : (
          <motion.div 
            className="chapters-grid"
            variants={staggerContainer}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true, amount: 0.2 }}
          >
            {chapters.map((chapter, index) => (
              <motion.div 
                key={chapter.id}
                className="chapter-card glass-animated"
                variants={staggerItem}
                whileHover={{ 
                  y: -5,
                  transition: { duration: 0.3 }
                }}
              >
                <div className="chapter-header">
                  <span className="chapter-number">{chapter.id}</span>
                  <span className="chapter-revelation-type">{chapter.revelation_type}</span>
                </div>
                <div className="chapter-content">
                  <h3 className="chapter-name">{chapter.name_simple}</h3>
                  <p className="chapter-translation">"{chapter.translated_name.name}"</p>
                  <p className="chapter-verses">{chapter.verses_count} verses</p>
                </div>
              </motion.div>
            ))}
          </motion.div>
        )}
      </div>
    </section>
  );
};

export default ChaptersSection;
