// src/components/sections/ChaptersSection.tsx
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { fadeInUp } from '../../hooks/useAnimations';
import './ChaptersSection.css';

interface Chapter {
  id: number;
  name_simple: string;
  name_arabic: string;
  translated_name: {
    language_name: string;
    name: string;
  };
  verses_count: number;
  revelation_place: string;
}

const ChaptersSection: React.FC = () => {
  const [chapters, setChapters] = useState<Chapter[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchChapters = async () => {
      try {
        const response = await fetch('https://api.quran.com/api/v4/chapters');
        if (!response.ok) {
          throw new Error('Failed to fetch chapters');
        }
        const data = await response.json();
        setChapters(data.chapters || []);
      } catch (err) {
        console.error('Error fetching chapters:', err);
        setError('Failed to load chapters');

        // Fallback data
        const fallbackChapters: Chapter[] = [
          { id: 1, name_simple: "Al-Fatihah", name_arabic: "الفاتحة", translated_name: { language_name: "english", name: "The Opener" }, verses_count: 7, revelation_place: "makkah" },
          { id: 2, name_simple: "Al-Baqarah", name_arabic: "البقرة", translated_name: { language_name: "english", name: "The Cow" }, verses_count: 286, revelation_place: "madinah" },
          { id: 3, name_simple: "Ali 'Imran", name_arabic: "آل عمران", translated_name: { language_name: "english", name: "Family of Imran" }, verses_count: 200, revelation_place: "madinah" },
          { id: 4, name_simple: "An-Nisa", name_arabic: "النساء", translated_name: { language_name: "english", name: "The Women" }, verses_count: 176, revelation_place: "madinah" },
          { id: 5, name_simple: "Al-Ma'idah", name_arabic: "المائدة", translated_name: { language_name: "english", name: "The Table Spread" }, verses_count: 120, revelation_place: "madinah" },
          { id: 6, name_simple: "Al-An'am", name_arabic: "الأنعام", translated_name: { language_name: "english", name: "The Cattle" }, verses_count: 165, revelation_place: "makkah" },
          { id: 7, name_simple: "Al-A'raf", name_arabic: "الأعراف", translated_name: { language_name: "english", name: "The Heights" }, verses_count: 206, revelation_place: "makkah" },
          { id: 8, name_simple: "Al-Anfal", name_arabic: "الأنفال", translated_name: { language_name: "english", name: "The Spoils of War" }, verses_count: 75, revelation_place: "madinah" },
          { id: 9, name_simple: "At-Tawbah", name_arabic: "التوبة", translated_name: { language_name: "english", name: "The Repentance" }, verses_count: 129, revelation_place: "madinah" },
          { id: 10, name_simple: "Yunus", name_arabic: "يونس", translated_name: { language_name: "english", name: "Jonah" }, verses_count: 109, revelation_place: "makkah" }
        ];
        setChapters(fallbackChapters);
      } finally {
        setLoading(false);
      }
    };

    fetchChapters();
  }, []);

  if (loading) {
    return (
      <section className="chapters-section">
        <div className="container">
          <div className="loading-state">
            <div className="spinner"></div>
            <p>Loading Quran chapters...</p>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="chapters-section">
        <div className="container">
          <div className="error-state">
            <p>{error}</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="chapters-section">
      <div className="container">
        <motion.div
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          variants={fadeInUp}
          className="section-header"
        >
          <h2 className="section-title">Quran Chapters (Surahs)</h2>
          <p className="section-subtitle">Explore the 114 chapters of the Holy Quran</p>
        </motion.div>

        <div className="chapters-grid">
          {chapters.map((chapter, index) => (
            <motion.div
              key={chapter.id}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.05 }}
              className="chapter-card"
            >
              <div className="chapter-number">
                {chapter.id}
              </div>
              <div className="chapter-content">
                <h3 className="chapter-name">
                  {chapter.name_simple}
                </h3>
                <p className="chapter-arabic">
                  {chapter.name_arabic}
                </p>
                <p className="chapter-translation">
                  {chapter.translated_name.name}
                </p>
                <div className="chapter-meta">
                  <span className="verses-count">
                    {chapter.verses_count} verses
                  </span>
                  <span className={`revelation-place ${chapter.revelation_place}`}>
                    {chapter.revelation_place === 'makkah' ? 'Meccan' : 'Medinan'}
                  </span>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ChaptersSection;
