// src/components/Footer.tsx
import React from 'react';
import { motion } from 'framer-motion';
import { staggerItem, fadeInUp } from '../hooks/useAnimations';
import '../styles/globals.css';

const Footer: React.FC = () => {
  return (
    <footer className="footer glass">
      <div className="container">
        <div className="footer-grid">
          {/* Left Column - About */}
          <motion.div 
            className="footer-section"
            variants={staggerItem}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h3 
              className="footer-title red-accent"
              variants={fadeInUp}
            >
              About QuranHub
            </motion.h3>
            <motion.p 
              className="footer-text"
              variants={fadeInUp}
              transition={{ delay: 0.1 }}
            >
              Providing access to the Holy Quran with beautiful interface and smooth animations. 
              Explore verses, Surahs, and Islamic resources with our aesthetic platform.
            </motion.p>
            <motion.div 
              className="social-links"
              variants={fadeInUp}
              transition={{ delay: 0.2 }}
            >
              <a href="#" className="social-link">Facebook</a>
              <a href="#" className="social-link">Twitter</a>
              <a href="#" className="social-link">Instagram</a>
            </motion.div>
          </motion.div>

          {/* Right Column - Links */}
          <motion.div 
            className="footer-section"
            variants={staggerItem}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h3 
              className="footer-title red-accent"
              variants={fadeInUp}
            >
              Quick Links
            </motion.h3>
            <motion.ul 
              className="footer-links"
              variants={staggerItem}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
            >
              {[
                { name: 'Home', url: '#' },
                { name: 'Surahs', url: '#' },
                { name: 'Juz', url: '#' },
                { name: 'About', url: '#' },
                { name: 'Resources', url: '#' },
                { name: 'Contact', url: '#' }
              ].map((link, index) => (
                <motion.li 
                  key={link.name}
                  variants={fadeInUp}
                  transition={{ delay: 0.1 * index }}
                >
                  <a href={link.url} className="footer-link">
                    {link.name}
                  </a>
                </motion.li>
              ))}
            </motion.ul>
          </motion.div>
        </div>

        <motion.div 
          className="footer-bottom"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ delay: 0.3 }}
        >
          <p>Â© {new Date().getFullYear()} QuranHub. All rights reserved.</p>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
