// src/components/Navigation.tsx
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { staggerItem, fadeInUp } from '../hooks/useAnimations';
import '../styles/globals.css';

const Navigation: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="slim-navbar">
      <div className="container flex justify-between items-center py-3">
        <motion.div 
          className="logo"
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1 className="text-xl font-bold red-accent">QuranHub</h1>
        </motion.div>

        {/* Desktop Navigation */}
        <div className="nav-links hidden md:flex">
          <motion.ul 
            className="flex space-x-6"
            variants={staggerItem}
            initial="hidden"
            animate="visible"
          >
            {['Home', 'Surahs', 'Juz', 'About', 'Resources'].map((item, index) => (
              <motion.li 
                key={item}
                variants={staggerItem}
                initial="hidden"
                animate="visible"
                transition={{ delay: index * 0.1 }}
              >
                <a 
                  href="#" 
                  className="text-gray-600 hover:text-red-600 transition-colors duration-300 font-medium"
                >
                  {item}
                </a>
              </motion.li>
            ))}
          </motion.ul>
        </div>

        {/* Mobile Menu Button */}
        <div className="md:hidden">
          <button 
            onClick={toggleMenu}
            className="focus:outline-none z-50"
            aria-label="Toggle menu"
          >
            <div className="space-y-1.5">
              <motion.span 
                className={`block w-6 h-0.5 bg-red-600 transition-transform duration-300 ${isMenuOpen ? 'rotate-45 translate-y-1.5' : ''}`}
                animate={isMenuOpen ? { rotate: 45, y: 5 } : { rotate: 0, y: 0 }}
                transition={{ duration: 0.3 }}
              ></motion.span>
              <motion.span 
                className={`block w-6 h-0.5 bg-red-600 transition-opacity duration-300 ${isMenuOpen ? 'opacity-0' : 'opacity-100'}`}
                animate={isMenuOpen ? { opacity: 0 } : { opacity: 1 }}
                transition={{ duration: 0.3 }}
              ></motion.span>
              <motion.span 
                className={`block w-6 h-0.5 bg-red-600 transition-transform duration-300 ${isMenuOpen ? '-rotate-45 -translate-y-1.5' : ''}`}
                animate={isMenuOpen ? { rotate: -45, y: -5 } : { rotate: 0, y: 0 }}
                transition={{ duration: 0.3 }}
              ></motion.span>
            </div>
          </button>
        </div>
      </div>

      {/* Mobile Navigation Menu */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.div 
            className="mobile-menu md:hidden absolute top-0 left-0 w-full h-screen bg-white z-40 pt-16"
            initial={{ opacity: 0, x: -300 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -300 }}
            transition={{ type: "spring", damping: 25 }}
          >
            <div className="container">
              <motion.ul 
                className="flex flex-col space-y-4 py-4"
                variants={staggerItem}
                initial="hidden"
                animate="visible"
              >
                {['Home', 'Surahs', 'Juz', 'About', 'Resources'].map((item, index) => (
                  <motion.li 
                    key={item}
                    variants={staggerItem}
                    initial="hidden"
                    animate="visible"
                    transition={{ delay: index * 0.1 }}
                  >
                    <a 
                      href="#" 
                      className="text-lg text-gray-700 hover:text-red-600 transition-colors duration-300 font-medium py-2 block"
                      onClick={() => setIsMenuOpen(false)}
                    >
                      {item}
                    </a>
                  </motion.li>
                ))}
              </motion.ul>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
};

export default Navigation;
