// src/components/Footer.tsx
import React from 'react';
import { motion } from 'framer-motion';
import { staggerItem, fadeInUp } from '../hooks/useAnimations';
import '../styles/globals.css';

const Footer: React.FC = () => {
  const quickLinks = [
    { name: 'Home', url: '#' },
    { name: 'Surahs', url: '#' },
    { name: 'Juz', url: '#' },
    { name: 'About', url: '#' },
    { name: 'Resources', url: '#' },
    { name: 'Contact', url: '#' }
  ];

  return (
    <footer className="simple-footer">
      <div className="container">
        <div className="footer-content">
          <motion.div 
            className="quick-links-section"
            variants={staggerItem}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
          >
            <motion.h3 
              className="quick-links-title"
              variants={fadeInUp}
            >
              Quick Links
            </motion.h3>
            <div className="circular-links">
              {quickLinks.map((link, index) => (
                <motion.a
                  key={link.name}
                  href={link.url}
                  className="circular-link glass-animated"
                  variants={fadeInUp}
                  whileHover={{ 
                    scale: 1.1,
                    y: -5,
                    backgroundColor: 'var(--primary-red)',
                    color: 'white',
                    transition: { duration: 0.3 }
                  }}
                  whileTap={{ scale: 0.95 }}
                  transition={{ duration: 0.3 }}
                >
                  {link.name}
                </motion.a>
              ))}
            </div>
          </motion.div>
        </div>

        <motion.div 
          className="footer-bottom"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ delay: 0.3 }}
        >
          <p className="footer-text">
            Â© {new Date().getFullYear()} QuranHub. All rights reserved.
          </p>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
